import React, { useState, useEffect } from "react";
import axios from "axios";
import Country from "./Country.js";
import Filter from "./components/Filter.js";
import Persons from "./components/Persons.js";

const App = () => {
  const [countries, setCountries] = useState([]);
  const [CurrentCountry, setCurrentCountry] = useState([]);

  useEffect(() => {
    console.log("in axios");
    axios.get("https://restcountries.eu/rest/v2/all").then((Response) => {
      console.log("promise successful !", Response);

      let countries = [];
      Response.data.forEach((element) => {
        countries.push(element);
      });
      console.log(countries);
      setCountries(countries);
    });
  }, []);

  const [searchResult, setNewSearchResult] = useState([]);

  // handlers
  const search = ({ CurrentCountry }) => {
    let i = 0;
    const result = [];
    while (i < countries.length && result.length <= 11) {
      const country = countries[i];
      if (country.name.toLowerCase().includes(query.toLowerCase())) {
        result.push(country);
      }
      i++;
    }
    if (result.length === 1) {
      setCurrentCountry(result[0]);
    }
    return result;
  };

  const HandleQueryChange = (event) => {
    const query = event.target.value;
    if (query) {
      console.log("in search");
      let result = search(query);
      setNewSearchResult(result);
      console.log(result);
    }
  };

  return (
    <div>
      <Filter handler={HandleQueryChange} />
      <Persons NamesToShow={searchResult} />
      <Country CurrentCountry={CurrentCountry} />
    </div>
  );
};

export default App;
