import React, { useState } from "react";

const App = () => {
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456" },
    { name: "Ada Lovelace", number: "39-44-5323523" },
    { name: "Dan Abramov", number: "12-43-234345" },
    { name: "Mary Poppendieck", number: "39-23-6423122" }
  ]);
  const [{ newName, newNuber }, setNewContact] = useState();

  const HandleChange = (event) => {
    setNewContact({ name: event.target.value, number: "" });
  };

  const addContact = (event) => {
    event.preventDefault();
    if (!persons.some((person) => person.name === newName)) {
      setNewContact(persons.concat({ name: newName, number: newNuber }));
    } else {
      window.alert(`${newName} is already added to phonebook`);
    }
  };

  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
          name: <input value={newName} onChange={HandleChange} required />
        </div>
        <div>
          number: <input required />
        </div>
        <div>
          <button type="submit" onClick={addContact}>
            add
          </button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        {persons.map((person) => (
          <h3 key={person.name + Math.random() * 123}>
            {person.name} {person.number}
          </h3>
        ))}
      </div>
    </div>
  );
};

export default App;
