import React, { useState } from "react";

const App = () => {
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456" },
    { name: "Ada Lovelace", number: "39-44-5323523" },
    { name: "Dan Abramov", number: "12-43-234345" },
    { name: "Mary Poppendieck", number: "39-23-6423122" }
  ]);

  const [searchResult, setNewSearchResult] = useState([]);
  const [isSearching, setisSearching] = useState(true);

  const [NewContact, setNewContact] = useState({
    newName: " ",
    newNuber: " "
  });

  const HandleQueryChange = (event) => {
    const query = event.target.value;
    if (query) {
      let result = persons.filter((person) =>
        person.name.toLowerCase().includes(query.toLowerCase())
      );
      setisSearching(true);
      setNewSearchResult(result);
    } else {
      setisSearching(false);
    }
  };

  const HandleNameChange = (event) => {
    const temp = { ...NewContact };
    setNewContact({ name: event.target.value, number: temp.number });
  };

  const HandleNumberChange = (event) => {
    const temp = { ...NewContact };
    setNewContact({ name: temp.name, number: event.target.value });
  };

  const addContact = (event) => {
    event.preventDefault();
    if (!persons.some((person) => person.name === NewContact.name)) {
      setPersons(persons.concat(NewContact));
    } else {
      window.alert(`${NewContact.name} is already added to phonebook`);
    }
  };

  const NamesToShow = isSearching ? searchResult : persons;
  console.log(searchResult);

  return (
    <div>
      <h2>Phonebook</h2>
      filter shown with : <input onChange={HandleQueryChange} />
      <form>
        <h2>add a new</h2>
        <div>
          name:
          <input onChange={HandleNameChange} />
        </div>
        <div>
          number:
          <input
            value={NewContact.number || " "}
            onChange={HandleNumberChange}
          />
        </div>
        <div>
          <button type="submit" onClick={addContact}>
            add
          </button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        {NamesToShow.map((person) => (
          <h4 key={person.name + Math.random() * 123}>
            {person.name} {person.number}
          </h4>
        ))}
      </div>
    </div>
  );
};

export default App;
