import React, { useState } from "react";

const App = () => {
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456" },
    { name: "Ada Lovelace", number: "39-44-5323523" },
    { name: "Dan Abramov", number: "12-43-234345" },
    { name: "Mary Poppendieck", number: "39-23-6423122" }
  ]);

  const [NewContact, setNewContact] = useState({
    newName: " ",
    newNuber: " "
  });

  const HandleNameChange = (event) => {
    const temp = { ...NewContact };
    setNewContact({ name: event.target.value, number: temp.number });
  };

  const HandleNumberChange = (event) => {
    const temp = { ...NewContact };
    setNewContact({ name: temp.name, number: event.target.value });
  };

  const addContact = (event) => {
    event.preventDefault();
    if (!persons.some((person) => person.name === NewContact.name)) {
      setPersons(persons.concat(NewContact));
    } else {
      window.alert(`${NewContact.name} is already added to phonebook`);
    }
  };

  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
          name:
          <input value={NewContact.name || " "} onChange={HandleNameChange} />
        </div>
        <div>
          number:
          <input
            value={NewContact.number || " "}
            onChange={HandleNumberChange}
          />
        </div>
        <div>
          <button type="submit" onClick={addContact}>
            add
          </button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        {persons.map((person) => (
          <h5 key={person.name + Math.random() * 123}>
            {person.name} {person.number}
          </h5>
        ))}
      </div>
    </div>
  );
};

export default App;
